import { SET_USER_INFO } from '../action/app';

export default function userReducer(
  state = {
    userInfo: {},
  },
  action
) {
  switch (action.type) {
    case SET_USER_INFO:
      return {
        ...state,
        userInfo: action.payload,
      };
    default:
      return state;
  }
}
