import { call, put, takeLatest } from 'redux-saga/effects';
import { getPosts, getCommentsByPost } from '../util/js/APIs.js';

function* watchGetPosts() {
  const result = yield call(getPosts);
  yield put({ type: 'SET_GLOBAL_LOADING', payload: false });
  if (result && result.status === 200) {
    console.log(2);
    yield put({ type: 'SET_POSTS', payload: result?.data });
  }
}

// function* watchGetUser() {
//   yield put({ type: 'GET_USER_SUCCESS' });
// }

function* Saga() {
  yield takeLatest('GET_POSTS', watchGetPosts);
  // yield takeLatest('GET_USER', watchGetUser);
}

export default Saga;
