export default function appReducer(
  state = {
    globalLoading: false,
  },
  action
) {
  switch (action.type) {
    case "SET_GLOBAL_LOADING":
      return {
        ...state,
        userInfo: action.payload,
      };
    default:
      return state;
  }
}
