import { get } from './APICaller';
import { API_URL } from './constant';

//#region Post
export const getPosts = (data) => {
  return get(`${API_URL}/user/get-posts`, data);
}
//#endregion

//#region Comment
export const getCommentsByPost = (data) => {
  return get(`${API_URL}/comment/get-comment`, data);
}
//#endregion

//#region User
export const getUser = (data) => {
  return get(`${API_URL}/user/get-user`, data);
}
//#endregion
