import {
  SET_GLOBAL_LOADING,
  SET_USER_INFO,
} from '../action/app';

export default function userReducer(
  state = {
    globalLoading: false,
    userInfo: {},
  },
  action
) {
  switch (action.type) {
    case SET_USER_INFO:
      return {
        ...state,
        userInfo: action.payload,
      };
    default:
      return state;
  }
}
