import React, { useState } from 'react';
import styles from './styles.module.css';
import Tag from 'common/Tag';
import Comment from 'common/Comment';
import { tags } from 'util/js/constant';

export default function Post({
  title = '',
  author = '',
  timestamp = 0,
  content = '',
  id,
}) {
  // #region    VARIABLES //////////////////////////
  //////////////////////////////////////////////////
  const [expand, setExpand] = useState(false);
  const date = new Date(timestamp / 1000);
  //////////////////////////////////////////////////
  // #endregion VARIABLES //////////////////////////

  // #region    useEffect //////////////////////////
  //////////////////////////////////////////////////

  //////////////////////////////////////////////////
  // #endregion useEffect //////////////////////////

  // #region    FUNCTIONS //////////////////////////
  //////////////////////////////////////////////////

  //////////////////////////////////////////////////
  // #endregion FUNCTIONS //////////////////////////

  // #region    VIEWS //////////////////////////////
  //////////////////////////////////////////////////
  const renderTags = () => {
    return tags.map((tag, index) => {
      return <Tag key={index} name={tag.name} color={tag} borderColor bgColor />;
    });
  };
  //////////////////////////////////////////////////
  // #endregion VIEWS //////////////////////////////
  return (
    <div>
      <div className={`${styles.title}`}>{title}</div>
      <div className={`${styles.detailCtn}`}>
        <div className={`${styles.detail}`}>
          <p className={`${styles.info} mb-2`}>Author: {author}</p>
          <p className={`${styles.info}`}>
            Created at:{' '}
            {date.toLocaleString('en-US', {
              month: 'short',
              day: 'numeric',
              year: 'numeric',
            })}
          </p>
        </div>
        <div className={`${styles.tag}`}>
          {renderTags()}
        </div>
      </div>
      <div className={`${styles.content}`}>{content}</div>
      <div
        className={`${styles.commentBtn}`}
        onClick={() => setExpand(!expand)}
      >
        <button onClick={() => setExpand(!expand)}>2 replies</button>
      </div>
      {expand && (
        <div className={`${styles.commentCtn}`}>
          <Comment
            author="John Smith"
            time="Sep 20, 2018"
            content="Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem"
          />
          <Comment
            author="John Smith"
            time="Sep 20, 2018"
            content="Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem"
          />
          <Comment
            author="John Smith"
            time="Sep 20, 2018"
            content="Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem Lorem Lorem lorem"
          />
        </div>
      )}
    </div>
  );
}
