import React, { useEffect } from 'react';
import { ListPost } from './Components/ListPost';
import axios from 'axios';
import { useSelector, useDispatch} from 'react-redux';
import { setData } from './actions';
import { Pagination } from './Components/Pagination';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import { Post } from './Components/Post';

const urlPosts = 'https://jsonplaceholder.typicode.com/posts';

function App() {
  const dispatch = useDispatch();
  const posts = useSelector(state => state.posts);
  const currentPage = useSelector(state => state.currentPage);
  const postsPerPage = useSelector(state => state.postsPerPage);

  const getData = async() => {

    const data = await axios({
      method: "get",
      url: urlPosts
    }).then(result => {
      dispatch(setData(result.data));
    });
  }

  useEffect(() => {
    getData();
  }, []);

  const indexOfLastPage = currentPage * postsPerPage;
  const indexOfFirstPage = indexOfLastPage - postsPerPage;
  const currentPosts = posts.slice(indexOfFirstPage, indexOfLastPage);



  return (

    <>
    <Router>
      <Switch>
          <Route path="/detail/:id">
           <Post/>
          </Route>
          <Route exact path="/">
            <div className="container-fluid">
              <nav className="navbar navbar-dark bg-primary">
                <div className="container-fluid">
                  <form className="d-flex">
                    <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"/> 
                    <button className="btn btn-outline-light text-dark" type="submit">Search</button>
                  </form>
                </div>
              </nav>

              <p className="h2 mt-5 mb-3 text-danger text-center">LIST OF POSTS ON HOME PAGE "Page {currentPage}"</p>
              {
                currentPosts.map(post => {
                  return <ListPost post={post} key={post.id} />;
                })
              }
              <Pagination />
            </div>
          </Route>
        </Switch>
    </Router>

    </>
  );
}

export default App;
