import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { useSelector } from 'react-redux';

export const Post = () => {
    const [user, setUser] = useState('');
    const userId = useSelector(state => state.postDetail.userId);
    const urlUser = `https://jsonplaceholder.typicode.com/users`;


    function randomDate(start, end) {
        const date = new Date(start.getTime() + Math.random() * (end.getTime() - start.getTime()));
        const newDate = date.getFullYear()+'/'+(date.getMonth()+1)+'/'+date.getDate(); 
        return newDate;
    }

    const getUser = async() => {
        const data = await axios({
            method: 'get',
            url: urlUser
        }).then(result => {
            const existUser = result.data.find(us => us.id === userId);
            if(existUser) {
                setUser(existUser.name);
            }
        })
    }

    useEffect(() => {
        getUser();
    },[])

    return (
        <div class="container-fluid">
            <nav class="navbar navbar-light bg-primary">
                <div class="container-fluid">
                    <Link to="/" class="navbar-brand text-dark">
                    <i class="fas fa-home"></i> Home
                    </Link>
                </div>
            </nav>
            
            <div class="row">
                <div class="col-md-4">
                    <img src="https://source.unsplash.com/random" class="card-img-top" alt="..." />
                </div>
                <div class="col-md-8">
                    <div class="card bg-light border-light">
                        <div class="card-body">
                            <h2 class="card-title text-primary text-center">title</h2>
                            <h6>Author: {user}</h6>
                            <p>
                                <em>
                                    Date: {randomDate(new Date(2018, 0, 1), new Date())}
                                </em> 
                            </p>
                            <p class="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                        </div>
                        
                        <div class="card-body">
                            <h6>
                                Comment: <em>6 <i class="fas fa-comments"></i></em>
                            </h6>
                            <ul class="list-group list-group-flush">
                                <li class="list-group-item">An item</li>
                                <li class="list-group-item">A second item</li>
                                <li class="list-group-item">A third item</li>
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
