import React, { useEffect, useRef, useState } from 'react';
import {Pagination} from './Pagination';
import { ListPost } from './ListPost';
import { useDispatch, useSelector } from 'react-redux';
import { useParams } from 'react-router';
// import {setSearchPosts} from '../actions';


export const Page = () => {
  const [searchPosts, setSearchPosts] = useState(null);
  let [currentPosts, setCurrentPosts] = useState([]);
  const searchRef = useRef();

    const currentPage = useSelector(state => state.currentPage);
    const dispatch = useDispatch();

    const postsPerPage = useSelector(state => state.postsPerPage);
    const posts = useSelector(state => state.posts);

    const indexOfLastPage = currentPage * postsPerPage;
    const indexOfFirstPage = indexOfLastPage - postsPerPage;
    if(searchPosts) {
      currentPosts = searchPosts.slice(indexOfFirstPage, indexOfLastPage);
    }
    else {
      currentPosts = posts.slice(indexOfFirstPage, indexOfLastPage);
    }
    // const currentPosts = posts.slice(indexOfFirstPage, indexOfLastPage);

    const handleSearch = () => {
      // console.log(searchRef.current.value);
      const search = searchRef.current.value.trim();
      const newPosts = posts.filter(post => post.title.indexOf(search) !== -1);
      // dispatch(setSearchPosts(newPosts));
      setSearchPosts(newPosts);
      console.log(newPosts);
    }

    return (
        <div className="container-fluid">
              <nav className="navbar navbar-dark bg-primary">
                <div className="container-fluid input-group" style={{width: '500px'}}>
                  
                    <input className="form-control me-2" 
                    type="search" placeholder="Search" aria-label="Search"
                      ref={searchRef}
                    /> 
                    <button className="btn btn-outline-light text-dark" 
                    type="submit"  onClick={() => handleSearch()}
                    >Search</button>
                  
                </div>
              </nav>
              
              <p className="h2 mt-5 mb-3 text-danger text-center">LIST OF POSTS ON HOME PAGE "Page {currentPage}"</p>
              {
                currentPosts.map(post => {
                  return <ListPost post={post} key={post.id} />;
                })
              }
              <Pagination />
            </div>
    );
}
