import axios from 'axios';
import React, { useEffect, useState } from 'react';

export const ListPost = ({post}) => {
    const {title, id, userId, body} = post;
    const [cmts, setCmts] = useState(null);
    const [user, setUser] = useState('');
    const urlCmt = `https://jsonplaceholder.typicode.com/posts/${id}/comments`;
    const urlUser = `https://jsonplaceholder.typicode.com/users`;

    const getCmt = async() => {
        const data = await axios({
            method: 'get',
            url: urlCmt
        }).then(result => {
            setCmts(result.data);
        })
    }

    const handleCmt = (id) => {
        document.getElementById(`ul-${id}`).style.display = "block";
    }

    const getUser = async() => {
        const data = await axios({
            method: 'get',
            url: urlUser
        }).then(result => {
            const existUser = result.data.find(us => us.id === userId);
            if(existUser) {
                setUser(existUser.name);
            }
        })
    }

    useEffect(() => {
        getCmt();
        getUser();
    }, [])
    
    function randomDate(start, end) {
        const date = new Date(start.getTime() + Math.random() * (end.getTime() - start.getTime()));
        const newDate = date.getFullYear()+'/'+(date.getMonth()+1)+'/'+date.getDate(); 
        return newDate;
    }


    return (
        <>
        <div class="card mb-5">
            <div class="card-body">
            <h4 class="card-title text-primary">{title}</h4>
                <h6>Author: {user}</h6>
                <p><em>Date: {randomDate(new Date(2018, 0, 1), new Date())}</em></p>
                <p class="card-text">{body}</p>
                <a href="#" class="btn btn-success mb-3"><i class="fas fa-info-circle"></i> Detail post</a><br/>
                <h6>Comment: <em>{cmts && cmts.length} <i class="fas fa-comments"></i></em></h6>
                <button class="btn btn-primary" onClick={() => handleCmt(id)}><i class="fas fa-eye"></i> see comment</button>
                <ul class="list-group mt-3" id={`ul-${id}`} style={{display: "none"}}>
                
                {
                    cmts && cmts.map((cmt, index) => {
                        return(<li key={index} class="list-group-item"><i class="fas fa-bullhorn"></i> {cmt.body}</li>);
                    })
                }
                
                </ul>
            </div>
        </div>
        </>
    );
}
