import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { setPostDetail } from '../actions';
import { getCmts } from '../actions';

export const ListPost = ({post}) => {
    const {title, id, userId, body} = post;
    // const [cmts, setCmts] = useState(null);
    const users = useSelector(state => state.users);
    // const [user, setUser] = useState('');
    // const urlCmt = `https://jsonplaceholder.typicode.com/posts/${id}/comments`;
    // const urlUser = `https://jsonplaceholder.typicode.com/users`;
    const user = users.find(u => u.id === userId);
    const dispatch = useDispatch();

    // const getCmt = async() => {
    //     const data = await axios({
    //         method: 'get',
    //         url: urlCmt
    //     }).then(result => {
    //         setCmts(result.data);
    //     })
    // }

    const handleCmt = (id) => {
        const ele = document.querySelectorAll(`#ul-${id} .list-group-item`);
        for(let i = 0; i < ele.length; i++) {
            ele[i].style.display = "block";
        }
    }

    // const getUser = async() => {
    //     const data = await axios({
    //         method: 'get',
    //         url: urlUser
    //     }).then(result => {
    //         const existUser = result.data.find(us => us.id === userId);
    //         if(existUser) {
    //             setUser(existUser.name);
    //         }
    //     })
    // }

    // const userName = users.find(user => user.id === userId);
    // console.log(users);
    
    // console.log(user);

    const cmts = [];

    useEffect(() => {
        // getCmt();
        cmts = dispatch(getCmts(id));
        // getUser();
    }, [])
    
    function randomDate(start, end) {
        const date = new Date(start.getTime() + Math.random() * (end.getTime() - start.getTime()));
        const newDate = date.getFullYear()+'/'+(date.getMonth()+1)+'/'+date.getDate(); 
        return newDate;
    }


    return (
        <>
        <div className="card mb-5 bg-light border-primary">
            <div className="card-body">
                <h4 className="card-title text-primary text-center">{title}</h4>
                <h6>Author: {user.name}</h6>
                <p>
                    <em>
                        Date: {randomDate(new Date(2018, 0, 1), new Date())} 
                    </em> 
                </p>
                <p className="card-text">{body.slice(0, 100)}</p>
                <Link to={`/detail/${id}`} 
                className="btn btn-success mb-3"
                onClick={() => dispatch(setPostDetail(post))}
                >
                    <i className="fas fa-info-circle"></i> Detail post
                </Link><br/>
                
                <button className="btn btn-primary mb-2" onClick={() => handleCmt(id)}>
                    <i className="fas fa-eye"></i> comments
                </button>
                <h6>
                    Comment: <em>{cmts && cmts.length} <i className="fas fa-comments"></i></em>
                </h6>
                <ul className="list-group mt-3" id={`ul-${id}`}>
                {
                    cmts && cmts.map((cmt, index) => {
                        if(index === 0) {
                            return(<li key={index} className="list-group-item"><i className="fas fa-bullhorn"></i> {cmt.body}</li>);
                        }
                        return(<li style={{display: "none"}} key={index} className="list-group-item"><i className="fas fa-bullhorn"></i> {cmt.body}</li>);
                    })
                }
                </ul>
            </div>
        </div>
        </>
    );
}
