import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
// import {setCurrentPage} from '../actions';
import { Link, useParams } from 'react-router-dom';

export const Pagination = () => {
    const dispatch = useDispatch();
    const postsPerPage = useSelector(state => state.postsPerPage);
    // const currentPage = useSelector(state => state.currentPage);
    const {num} = useParams();
    let currentPage = null;
    if(num) {
        currentPage = num;
    }
    else {
        currentPage = 1;
    }
    // const posts = useSelector(state => state.posts);
    const searchPosts = useSelector(state => state.searchPosts);
    const totalPost = searchPosts.length;
    const pageNumber = [];

    // const paginate = (num) => {
    //     dispatch(setCurrentPage(num));
    // }

    for(let i = 1;  i <= Math.ceil(totalPost / postsPerPage); i++) {
        pageNumber.push(i);
    }

    // const handleClick = (change) => {
    //     dispatch(setCurrentPage(currentPage + change));
    // }

    return (
        <nav aria-label="Page navigation example">
            <ul className="pagination justify-content-center">
                <li className={`page-item ${currentPage === 1 && "disabled"}`}>
                    <Link to={`/page/${currentPage-1}`} className="page-link" 
                    tabIndex="-1" aria-disabled="true"
                    >Previous</Link>
                </li>
                {
                    pageNumber.map((num, index) => {
                        {/* if(index === currentPage) {
                            
                        } */}
                        return <li key={index} className={`page-item ${index===currentPage-1 && "active"}`}>
                            <Link to={`/page/${num}`} className="page-link">{num}</Link>
                            {/* onClick={() => {paginate(num)}} */}
                        </li>;
                    })
                }
                <li className={`page-item ${currentPage >= pageNumber.length && "disabled"}`}>
                    <Link to={`/page/${currentPage+1}`} className="page-link">Next</Link>
                </li>
            </ul>
        </nav>
    );
}
