import { persistStore, persistReducer } from 'redux-persist';
import storage from 'redux-persist/lib/storage';
// import { createStore } from 'redux';
import { createStore, applyMiddleware, compose } from 'redux';
import createSagaMiddleware from 'redux-saga';
import postsSaga from '../saga';

const sagaMiddleware = createSagaMiddleware();

const initialState = {
    posts: [],
    currentPage: 1,
    postsPerPage: 10,
    postDetail: {},
    error: null,
}

const rootReducer = (state = initialState, action) => {
    switch(action.type){
        // case 'GET_POSTS_REQUEST':
        //     return {...state};
        // case 'GET_POSTS_SUCCESS':
        //     return {...state, posts: action.payload};
        // case 'GET_POSTS_FAILED':
        //     return {...state, error: action.message};
        case 'setData':
            return {...state, posts: action.payload};
        case 'setCurrentPage':
            return {...state, currentPage: action.payload};
        case 'setPostDetail':
            return {...state, postDetail: action.payload};
        default: 
            return state;
    }
}

const persistConfig = {
    key: 'root',
    storage,
}



export const persist_Reducer = persistReducer(persistConfig, rootReducer);

// export const store = createStore(persist_Reducer,
//     window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__());

export const store =  compose(
    applyMiddleware(sagaMiddleware),
    window.__REDUX_DEVTOOLS_EXTENSION__ && window.__REDUX_DEVTOOLS_EXTENSION__()
)(createStore)(persist_Reducer);

sagaMiddleware.run(postsSaga);

export const persistor = persistStore(store);

export default {rootReducer , persistor};
