import axios from 'axios';
import React, { useEffect, useState } from 'react';

export const ListPost = ({post}) => {
    const {title, id, userId, body} = post;
    const [cmts, setCmts] = useState(null);
    const urlCmt = `https://jsonplaceholder.typicode.com/posts/${id}/comments`;

    const getCmt = async() => {
        const data = await axios({
            method: 'get',
            url: urlCmt
        }).then(result => {
            setCmts(result.data);
        })
    }

    const handleCmt = (id) => {
        document.getElementById(`ul-${id}`).style.display = "block";
    }

    useEffect(() => {
        getCmt();
    }, [])
    

    return (
        <>
        <div class="card mb-5">
            <div class="card-body">
            <h5 class="card-title">{title}</h5>
                <h6>Author: {userId}</h6>
                <p><em>date:</em></p>
                <p class="card-text">{body}</p>
                <a href="#" class="btn btn-primary mb-3">Detail post</a><br/>
                <h6>Comment: <em>{cmts.length}</em></h6>
                <button class="btn btn-primary" onClick={() => handleCmt(id)}>All comment</button>
                <ul class="list-group mt-3" id={`ul-${id}`} style={{display: "none"}}>
                
                {
                    cmts && cmts.map((cmt, index) => {
                        return(<li key={index} class="list-group-item">{cmt.body}</li>);
                    })
                }
                </ul>
            </div>
        </div>
        </>
    );
}
