import React from 'react';
import { useDispatch, useSelector } from 'react-redux';
import {setCurrentPage} from '../actions';

export const Pagination = () => {
    const dispatch = useDispatch();
    const postsPerPage = useSelector(state => state.postsPerPage);
    const currentPage = useSelector(state => state.currentPage);
    const posts = useSelector(state => state.posts);
    const totalPost = posts.length;
    const pageNumber = [];

    const paginate = (num) => {
        dispatch(setCurrentPage(num));
    }

    for(let i = 1;  i <= Math.ceil(totalPost / postsPerPage); i++) {
        pageNumber.push(i);
    }

    

    return (
        <nav aria-label="Page navigation example">
            <ul class="pagination justify-content-center">
                <li class={`page-item ${currentPage === 1 && "disabled"}`}>
                    <a class="page-link" tabindex="-1" aria-disabled="true">Previous</a>
                </li>
                {
                    pageNumber.map((num, index) => {
                        {/* if(index === currentPage) {
                            
                        } */}
                        return <li key={index} class={`page-item ${index===currentPage-1 && "active"}`}>
                            <a class="page-link" onClick={() => {paginate(num)}}>{num}</a>
                        </li>;
                    })
                }
                <li class={`page-item ${currentPage === pageNumber.length && "disabled"}`}>
                    <a class="page-link">Next</a>
                </li>
            </ul>
        </nav>
    );
}
