import React, { useEffect } from 'react';
import { ListPost } from './Components/ListPost';
import axios from 'axios';
import { useSelector, useDispatch} from 'react-redux';
import { setData } from './actions';
import { Pagination } from './Components/Pagination';

const urlPosts = 'https://jsonplaceholder.typicode.com/posts';

function App() {
  const dispatch = useDispatch();
  const posts = useSelector(state => state.posts);
  const currentPage = useSelector(state => state.currentPage);
  const postsPerPage = useSelector(state => state.postsPerPage);

  const getData = async() => {

    const data = await axios({
      method: "get",
      url: urlPosts
    }).then(result => {
      dispatch(setData(result.data));
    });
  }

  useEffect(() => {
    getData();
  }, []);

  const indexOfLastPage = currentPage * postsPerPage;
  const indexOfFirstPage = indexOfLastPage - postsPerPage;
  const currentPosts = posts.slice(indexOfFirstPage, indexOfLastPage);



  return (
    <div className="container-fluid">
    <nav class="navbar navbar-dark bg-primary">
      <div class="container-fluid">
        <form class="d-flex">
          <input class="form-control me-2" type="search" placeholder="Search" aria-label="Search"/> 
          <button class="btn btn-outline-light text-dark" type="submit">Search</button>
        </form>
      </div>
    </nav>

      <p class="h2 mt-3 text-danger text-center">Posts "Page {currentPage}"</p>
      {
        currentPosts.map(post => {
          return <ListPost post={post} key={post.id} />;
        })
      }
      <Pagination />
    </div>
  );
}

export default App;
