const initialState = {
    posts: [],
    currentPage: 1,
    postsPerPage: 10,
    postDetail: null
}

const rootReducer = (state = initialState, action) => {
    switch(action.type){
        case 'setData':
            return {...state, posts: action.payload};
        case 'setCurrentPage':
            return {...state, currentPage: action.payload};
        case 'setPostDetail':
            return {...state, postDetail: action.payload};
        default: 
            return state;
    }
}

export default rootReducer;
