import React, { useEffect, useState } from 'react';
import { Link, useParams } from 'react-router-dom';
import axios from 'axios';
import { useSelector } from 'react-redux';

export const Post = () => {
    const [user, setUser] = useState('');
    const [cmts, setCmts] = useState(null);
    const postDetail = useSelector(state => state.postDetail);
    const userId = postDetail.userId;
    const id = useParams();
    const urlUser = `https://jsonplaceholder.typicode.com/users`;
    const urlCmt = 'https://jsonplaceholder.typicode.com/posts/'+ id +'/comments';


    function randomDate(start, end) {
        const date = new Date(start.getTime() + Math.random() * (end.getTime() - start.getTime()));
        const newDate = date.getFullYear()+'/'+(date.getMonth()+1)+'/'+date.getDate(); 
        return newDate;
    }
    
    const getCmt = async() => {
        const data = await axios({
            method: 'get',
            url: urlCmt
        }).then(result => {
            setCmts(result.data);
        })
    }

    const getUser = async() => {
        const data = await axios({
            method: 'get',
            url: urlUser
        }).then(result => {
            const existUser = result.data.find(us => us.id === userId);
            if(existUser) {
                setUser(existUser.name);
            }
        })
    }

    useEffect(() => {
        if(id) {
            getUser();
        getCmt();
        }
        console.log(id);
    },[id])

    return (
        <div className="container-fluid">
            <nav className="navbar navbar-light bg-primary">
                <div className="container-fluid">
                    <Link to="/" className="navbar-brand text-dark">
                    <i className="fas fa-home"></i> Home
                    </Link>
                </div>
            </nav>
            
            <div className="row">
                <div className="col-md-4">
                    <img src="https://source.unsplash.com/random" className="card-img-top" alt="..." />
                </div>
                <div className="col-md-8">
                    <div className="card bg-light border-light">
                        <div className="card-body">
                            <h2 className="card-title text-primary text-center">{postDetail.title}</h2>
                            <h5><i className="fas fa-pen-alt"></i> {user}</h5>
                            <p>
                                <em>
                                    <i className="far fa-calendar"></i> {randomDate(new Date(2018, 0, 1), new Date())}
                                </em> 
                            </p>
                            <h5 className="text-danger card-text">{postDetail.body}</h5>
                        </div>
                        
                        <div className="card-body">
                            <h6>
                                Comment: <em>{cmts && cmts.length} <i className="fas fa-comments"></i></em>
                            </h6>
                            <ul className="list-group list-group-flush">
                                
                                {
                                    cmts && cmts.map((cmt, index) => {
                                        return(<li key={index} className="list-group-item"><i className="fas fa-bullhorn"></i> {cmt.body}</li>);
                                    })
                                }
                            </ul>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}
